trees2d = {};
trees2d.normalVariate = function (a, b) {
	return a + (Math.random() * 2 + Math.random() * 2 + Math.random() * 2 - 3) / 3 * b / 2
};

/**
 * @typedef LeafTexture
 * @type {object}
 * @property {string} img - image
 * @property {number} targetWidthInPixel - targetWidthInPixel
 * @property {number} relativeProba - relativeProba
 */

/**
 * 
 * @param {HTMLCanvasElement} canvas 
 */
trees2d.tree = function (canvas) {
	this.canvas2d = canvas;
	/** @type {CanvasRenderingContext2D} */
	this.ctx2d = canvas.getContext("2d");
	this.string = "fF[+F][-F]";
	this.branchTexture;
	this.treePosY = 50;
	this.iterations = 0;
	this.angleMean = 20 / 180 * Math.PI;
	this.angleVariation = 10 / 180 * Math.PI;
	this.length = 30;
	this.lengthReduction = 0.8;
	this.thickness = 15;
	this.thicknessReduction = 0.65;
	this.rules = {
		F: { developsInto: ["f", "+fF", "-fF", "F[+FF][-FF]f", "[+F][-F]", "F[+fFF]", "F[-fFF]"] },
		f: { developsInto: ["f"] },
		"[": { developsInto: ["["] },
		"]": { developsInto: ["]"] },
		"+": { developsInto: ["+"] },
		"-": { developsInto: ["-"] }
	};
	/** @type {[LeafTexture]} */
	this.leafTextures = [];
	this.leafScaleVariation = 0.5;
	this.leafMinDepth = 0;
	this.leafProba = 0.5;
	this.leafScale = 1;
	this.leafTotalPerBranch = 1;
	this.leafProbaLighterMult = 0.5;
	this.curveXVariation = 10;
	this.curveYVariation = 5;
	this.shadowProba = 0.2, this.shadowAlpha = 0.025;
	this.shadowRadius = 40

	for (var ii = 0; ii < 3; ii++) {
		this.generate();
	}
};
trees2d.tree.prototype.addLeafTexture = function (a, b, c) {
	this.leafTextures.push({ img: a, targetWidthInPixel: b, relativeProba: c })
};
trees2d.tree.prototype.removeLeafTexture = function (a) {
	for (var b = 0; b < this.leafTextures.length; b++) {
		var c = this.leafTextures[b];
		if (c.img == a) {
			this.leafTextures.splice(b, 1);
			break
		}
	}
};
trees2d.tree.prototype.hasLeafTexture = function (a) {
	for (var b = 0; b < this.leafTextures.length; b++) {
		var c = this.leafTextures[b];
		if (c.img == a) {
			return true
		}
	}
	return false
};
trees2d.tree.prototype.totalLeafTextures = function () {
	return this.leafTextures.length
};
trees2d.tree.prototype.generate = function () {
	var treeStr = this.string;
	var newTressStr = "";
	for (var jj = 0; jj < treeStr.length; jj++) {
		var g = treeStr[jj];
		var ruleList = this.rules[g].developsInto;
		if (ruleList.length <= 0) {
			continue
		}
		var rule = ruleList[parseInt(Math.floor(Math.random() * ruleList.length))];
		newTressStr += rule
	}
	treeStr = newTressStr
	this.string = newTressStr;
	++this.iterations;
}
trees2d.tree.prototype.draw = function () {
	for (var jj = 0; jj < this.leafTextures.length; jj++) {
		var leafImg = this.leafTextures[jj];
		if (!leafImg.img.complete) {
			return leafImg.img.src.substring(0, 100) + " is not completely loaded, wait until all textures are loaded"
		}
		if (leafImg.img.naturalWidth === 0 || leafImg.img.naturalHeight === 0) {
			return leafImg.img.src.substring(0, 100) + " is not a valid image"
		}
	}
	if (!this.branchTexture.complete) {
		return this.branchTexture.src.substring(0, 100) + " is not completely loaded, wait until all textures are loaded"
	}
	if (this.branchTexture.naturalWidth === 0 || this.branchTexture.naturalHeight === 0) {
		return this.branchTexture.src.substring(0, 100) + " is not a valid image"
	}
	var canvasContext2d = this.ctx2d;
	var drawError;
	canvasContext2d.setTransform(1, 0, 0, 1, 0, 0);
	canvasContext2d.save();
	try {
		var treeStr = this.string;
		var w = 0.1;
		var f = this.iterations > 0 ? 1 / this.iterations : 1;
		var branchPattern = this.ctx2d.createPattern(this.branchTexture, "repeat");
		canvasContext2d.strokeStyle = branchPattern;
		canvasContext2d.lineWidth = this.thickness;
		canvasContext2d.lineCap = "round";
		var p = 1;
		var h = 0;
		canvasContext2d.clearRect(0, 0, this.canvas2d.width, this.canvas2d.height);
		canvasContext2d.translate(this.canvas2d.width / 2, this.canvas2d.height - this.treePosY);
		// canvasContext2d.scale(3, 3);
		for (var jj = 0; jj < treeStr.length; jj++) {
			var curChar = treeStr[jj];
			if (curChar == "f") {
				canvasContext2d.strokeStyle = branchPattern;
				canvasContext2d.beginPath();
				canvasContext2d.moveTo(0, 0);
				canvasContext2d.quadraticCurveTo(trees2d.normalVariate(0, this.curveXVariation), trees2d.normalVariate(0, this.curveYVariation) - this.length / 2, 0, -this.length);
				canvasContext2d.stroke();
				if (Math.random() <= this.shadowProba) {
					canvasContext2d.save();
					canvasContext2d.globalCompositeOperation = "source-atop";
					canvasContext2d.globalAlpha = this.shadowAlpha;
					canvasContext2d.beginPath();
					canvasContext2d.arc(0, 0, this.shadowRadius, 0, Math.PI * 2, false);
					canvasContext2d.fill();
					canvasContext2d.restore()
				}
				h++;
				canvasContext2d.translate(0, -this.length);
				if (this.leafTextures.length > 0 && p >= this.leafMinDepth && Math.random() <= this.leafProba) {
					for (var kk = 0; kk < this.leafTotalPerBranch; kk++) {
						canvasContext2d.save();
						if (Math.random() <= (p / this.iterations * this.leafProbaLighterMult)) {
							canvasContext2d.globalCompositeOperation = "lighter"
						}
						var leafImg = this.leafTextures[parseInt(Math.random() * this.leafTextures.length)];
						var scale = leafImg.targetWidthInPixel / leafImg.img.naturalWidth * this.leafScale;
						canvasContext2d.scale(scale * (1 + (Math.random() * 2 - 1) * this.leafScaleVariation), scale * (1 + (Math.random() * 2 - 1) * this.leafScaleVariation));
						canvasContext2d.rotate(Math.random() * Math.PI * 2);
						canvasContext2d.drawImage(leafImg.img, 0, 0);
						canvasContext2d.restore()
					}
				}
			} else if (curChar == "+") {
				canvasContext2d.rotate(trees2d.normalVariate(this.angleMean, this.angleVariation))
			} else if (curChar == "-") {
				canvasContext2d.rotate(-trees2d.normalVariate(this.angleMean, this.angleVariation))
			} else if (curChar == "[") {
				canvasContext2d.save();
				this.length *= this.lengthReduction;
				p++;
				w += f;
				canvasContext2d.lineWidth *= this.thicknessReduction
			} else if (curChar == "]") {
				this.length *= 1 / this.lengthReduction;
				p--;
				w -= f;
				canvasContext2d.restore()
			}
		}
	} catch (err) {
		drawError = err
	}
	canvasContext2d.restore();
	return drawError
};